declare module '*.eot' {
    const content: any;
    export default content;
}

declare module '*.woff' {
    const content: any;
    export default content;
}

declare module '*.woff2' {
    const content: any;
    export default content;
}

declare module '*.svg' {
    const content: string;
    export default content;
}

declare type Nullable<T> = null | T;

declare module '*.png' {
    const value: any;
    export default value;
}
